import * as React from 'react-bootstrap/node_modules/react';

import NavItem from 'react-bootstrap/NavItem';
import NavLink from 'react-bootstrap/NavLink';

import { BsPrefixComponent, SelectCallback } from 'react-bootstrap/helpers';

export interface NavProps {
  navbarBsPrefix?: string;
  cardHeaderBsPrefix?: string;
  variant?: 'tabs' | 'pills';
  activeKey?: unknown;
  defaultActiveKey?: unknown;
  fill?: boolean;
  justify?: boolean;
  onSelect?: SelectCallback;
  role?: string;
  navbar?: boolean;
  onKeyDown?: React.KeyboardEventHandler<this>;
}

declare class Nav<As extends React.ReactType = 'div'> extends BsPrefixComponent<
  As,
  NavProps
> {
  static Item: typeof NavItem;
  static Link: typeof NavLink;
}

export default Nav;
