import * as React from 'react-bootstrap/node_modules/react';

import TabContainer from 'react-bootstrap/TabContainer';
import TabContent from 'react-bootstrap/TabContent';
import TabPane from 'react-bootstrap/TabPane';

import { BsPrefixComponent } from 'react-bootstrap/helpers';

export interface TabProps extends React.ComponentPropsWithRef<typeof TabPane> {
  eventKey?: unknown;
  title: React.ReactNode;
  disabled?: boolean;
  tabClassName?: string;
}

declare class Tab<As extends React.ReactType = 'div'> extends BsPrefixComponent<
  As,
  TabProps
> {
  static Container: typeof TabContainer;
  static Content: typeof TabContent;
  static Pane: typeof TabPane;
}

export default Tab;
